INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (4, '麻省理工学院', 'Massachusetts Institute of Technology', '麻省理工学院（Massachusetts Institute of Technology），简称“麻省理工”（MIT），位于美国马萨诸塞州波士顿都市区剑桥市，主校区依查尔斯河而建，是世界著名私立研究型大学。麻省理工学院创立于1861年，早期侧重应用科学及工程学，在第二次世界大战后，麻省理工学院倚靠美国国防科技的研发需要而迅速崛起。在二战和冷战期间，麻省理工学院的研究人员对计算机、雷达以及惯性导航系统等科技发展作出了重要贡献。麻省理工学院素以顶尖的工程学和计算机科学而著名，拥有麻省理工人工智能实验室（MIT CSAIL）、林肯实验室（MIT Lincoln Lab）和麻省理工学院媒体实验室（MIT Media Lab），其研究人员发明了万维网、GNU系统、Emacs编辑器、RSA算法等等。 该校的计算机工程、电机工程等诸多工程学领域在2019-20年世界大学学术排名中位列世界前五，在2018-19年US News美国研究生院排名中位列工程学第一、计算机科学第一，与斯坦福大学、加州大学伯克利分校一同被称为工程科技界的学术领袖。 截止至2019年10月，麻省理工学院的校友、教职工及研究人员中，共产生了97位诺贝尔奖得主（世界第五）、8位菲尔兹奖得主（世界第八）以及26位图灵奖得主（世界第二）。麻省理工学院位列2020-21年度QS世界大学排名第一 、软科世界大学学术排名第四、THE世界大学排名第五。同时列2020U.S. News世界大学排名第二，2020THE世界大学声誉排名世界第二。', '大学（私立）', '美国', 1, 'Artificial Intelligence and Decision Making', '是', '学士', 'https://catalog.mit.edu/degree-charts/artifical-intelligence-decision-making-course-6-4/', 'School of Engineering', 'Department of Electrical Engineering and Computer Science (EECS), Computer Science and Artificial Intelligence Lab (CSAIL), the Laboratory for Information and Decision Systems (LIDS), the Microsystems and Technology Laboratories (MTL) and the Research Laboratory of Electronics (RLE)', NULL, 'https://mitadmissions.org/apply/', 'Bachelor of Science in Artificial Intelligence and Decision Making', '1 Year to Less than 5 Years', '否', '183-192units ', NULL, 'Our educational mission is to provide students with the strong mathematical and algorithmic foundations needed to build robust systems that can extrapolate from data to insights and decisions.', 'Engineering Ethos.Leadership.Versatility.Engagement.ABET Accreditation', '否', '是', '是', 'https://www.eecs.mit.edu/academics/undergraduate-programs/curriculum/6-4-artificial-intelligence-and-decision-making/', 'https://www.eecs.mit.edu/role/faculty-aid/?fwp_role=faculty-aid', NULL);
