INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (6, '斯坦福大学', 'Stanford University', '斯坦福大学（Stanford University），全名小利兰·斯坦福大学（Leland Stanford Junior University），简称“斯坦福”，位于美国加州旧金山湾区南部帕罗奥多市境内，临近高科技园区硅谷（Silicon Valley），是世界著名私立研究型大学。斯坦福大学于1885年成立，1891年开始正式招生，占地约33平方公里（8180英亩），是美国占地面积第六大的大学。斯坦福大学与旧金山北湾的加州大学伯克利分校共同构成了美国西部的学术中心，并负责运行管理SLAC国家加速器实验室、胡佛研究所等机构。据统计，截至2020年3月，共有83位斯坦福大学的校友、教授及研究人员曾获得诺贝尔奖（世界第七）、28位曾获得图灵奖（世界第一）、8位曾获得过菲尔兹奖（世界第八）。 2019-20年度位列U.S. News世界大学排名第3、THE世界大学声誉排名第3、自然指数排名第4 。2020-21年度位列QS世界大学排名第2、软科世界大学学术排名第2 、THE世界大学排名第4、莫斯科国际大学排名第7，斯坦福大学为硅谷的形成和崛起奠定了坚实的基础，培养了众多高科技公司的领导者，其中包括惠普、谷歌、雅虎、耐克、罗技、Snapchat、美国艺电公司、太阳微、NVIDIA、思科及LinkedIn等公司的创办人。 此外，斯坦福的校友涵盖30名富豪企业家及17名NASA太空员，亦为培养最多美国国会成员的高等院校之一。根据《福布斯》2010年盘点的亿万富豪最多的大学，斯坦福大学名列第二，亿万富豪数量达28位、仅次于哈佛大学。', '大学（私立）', '美国', 4, 'Computer Science-Artificial Intelligence (AI)', '否', '学士', 'https://www.cs.stanford.edu/academics/academics-bachelors-program', 'School of Engineering', 'Computer Science Department(CSD)', NULL, 'https://admission.stanford.edu/apply/', 'Bachelor of Science', '1 Year to Less than 5 Years', '否', '96units', 'https://www.cs.stanford.edu/bs-requirements-senior-project', 'to prepare students for a rapidly changing world.', '/', '否', '否', '是', 'https://www.cs.stanford.edu/courses-overview', 'https://www.cs.stanford.edu/people-cs/faculty-research/artificial-intelligence', 'https://ughb.stanford.edu/majors-minors/computer-science-program');
