INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (70, NULL, NULL, NULL, '政府组织', NULL, NULL, 'NSF Scholarships in Science, Technology, Engineering, and Mathematics Program  (S-STEM)', '否', '非学位项目', 'https://www.nsf.gov/pubs/2021/nsf21550/nsf21550.htm', NULL, NULL, NULL, NULL, NULL, 'Variable Time', NULL, NULL, NULL, 'The program seeks to 1) increase the number of low-income academically talented students with demonstrated financial need obtaining degrees in S-STEM eligible disciplines and entering the US workforce or graduate programs in STEM; 2) improve support mechanisms for future scientists, engineers, and technicians, with a focus on low-income academically talented students with demonstrated financial need; and 3) advance our understanding of how interventions or evidence-based curricular and co-curricular activities affect the success, retention, transfer, academic/career pathways, and graduation of low-income students in STEM.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
