INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (72, NULL, NULL, NULL, '政府组织', NULL, NULL, 'The American Rocketry Challenge', '否', '非学位项目', 'https://rocketcontest.org/', NULL, NULL, NULL, NULL, NULL, 'Variable Time', NULL, NULL, NULL, 'The American Rocketry Challenge is the world’s largest rocket contest with nearly 5,000 students nationwide competing each year. The contest gives middle and high school students the opportunity to design, build and launch model rockets and hands-on experience solving engineering problems.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
