INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (74, NULL, NULL, NULL, '私人机构', NULL, NULL, 'Bebras', '否', '非学位项目', 'https://www.bebraschallenge.org/', NULL, NULL, NULL, NULL, NULL, 'Multiple 45 Minutes Sessions', NULL, NULL, NULL, 'The Bebras? Computing Challenge introduces computational thinking to students. It is organized in over 30 countries and designed to get students all over the world excited about computing. Each participant gets 45 minutes to answer 15 multiple-choice questions that focus on computational and logical thinking. It is completed online in your own school and it shows to school and student how well their skills are developed.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (75, NULL, NULL, NULL, '大学', NULL, NULL, 'Hack Arizona', '否', '非学位项目', 'https://hackaz.io/', NULL, NULL, NULL, NULL, NULL, '3 Days', NULL, NULL, NULL, 'Hack Arizona is a student led organization fostering a world-class hacker culture in the Southwest and hosted at the University of Arizona. Teams of up to four people compete to win prizes; all while attending fun activities and lots of educational sessions.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (76, NULL, NULL, NULL, '政府组织', NULL, NULL, 'NIST NRC Postdoctoral Research Apprenticeship Program', '否', '非学位项目', 'https://www.nist.gov/iaao/academic-affairs-office/nist-nrc-postdoctoral-research-associateships-program', NULL, NULL, NULL, NULL, NULL, '2 Years', NULL, NULL, NULL, 'The NIST NRC Postdoctoral Program supports a nationwide competitive postdoctoral program administered in cooperation with the National Academies/National Research Council (NRC). The postdoctoral program brings research scientists and engineers of unusual promise and ability to perform advanced research related to the NIST mission, introduces the latest university research results and techniques to NIST scientific programs, strengthens mutual communication with university researchers, shares NIST unique research facilities with the U.S. scientific and engineering communities, and provides a valuable mechanism for the transfer of research results from NIST to the scientific and engineering communities.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
