INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (78, NULL, NULL, NULL, '政府组织', NULL, NULL, 'Colorado College Summer Internship Program', '否', '非学位项目', 'https://www.intelligencecareers.gov/icstudents.html', NULL, NULL, NULL, NULL, NULL, 'Summer', NULL, NULL, NULL, 'Students will gain hands-on work experience while building knowledge and skills in fundamental aspects of the NSA mission. Internship positions are offered in software engineering and target analysis.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (79, NULL, NULL, NULL, '政府组织', NULL, NULL, 'DOE Computational Science Graduate Fellowship', '否', '非学位项目', 'https://internships.fnal.gov/csgf/', NULL, NULL, NULL, NULL, NULL, 'Summer', NULL, NULL, NULL, 'Established in 1991, the Department of Energy Computational Science Graduate Fellowship (DOE CSGF) provides outstanding benefits and opportunities to students pursuing doctoral degrees in fields that use high-performance computing to solve complex science and engineering problems. The program fosters a community of energetic and committed Ph.D. students, alumni, DOE laboratory staff and other scientists who want to have an impact on the nation while advancing their research. Fellows come from diverse scientific and engineering disciplines but share a common interest in using computing in their research.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (80, NULL, NULL, NULL, '政府组织', NULL, NULL, 'DoD National Defense Science and Engineering Graduate Fellowship program', '否', '非学位项目', 'https://www.onr.navy.mil/en/Education-Outreach/undergraduate-graduate/NDSEG-graduate-fellowship', NULL, NULL, NULL, NULL, NULL, '36 Months', NULL, NULL, NULL, 'The National Defense Science and Engineering Graduate (NDSEG) Fellowship Program is a joint program of the United States Army, Navy and Air Force within the University Research Initiative (URI), designed to increase the number of U.S. citizens trained in disciplines of science and engineering important to defense goals. DoD awards approximately 100-150 new three-year graduate fellowships each year to individuals for study and research leading to doctoral degrees in, or closely related to, the disciplines of aeronautical and astronautical engineering; biosciences; chemical engineering; chemistry; cognitive, neural and behavioral sciences; electrical engineering; geosciences; civil engineering; computer and computational sciences; materials science and engineering; mathematics; mechanical engineering; naval architecture and ocean engineering; oceanography; and physics.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (81, NULL, NULL, NULL, '非盈利组织', NULL, NULL, 'EnviroHacks', '否', '非学位项目', 'https://caccoalition.org/envirohacks', NULL, NULL, NULL, NULL, NULL, 'Variable Time', NULL, NULL, NULL, 'Coalition Against Climate Change(CACC) is a youth based environmental organization that seeks to bring awareness to climate issues in our communities. Partnering with RoundPier, we are organizing EnviroHacks as a means for students to put their skills in STEM and public policy to design innovative solutions to some of the most pressing problems of today. EnviroHacks is a hackathon-like competition, where students(under the age of 18), will be able to compete either individually or in a team of up to 4 to design a solution to a pressing environmental issue.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (82, NULL, NULL, NULL, '政府组织', NULL, NULL, 'Summer Intern Program', '否', '非学位项目', 'https://apply.intelligencecareers.gov/student-tiles', NULL, NULL, NULL, NULL, NULL, '12 Weeks', NULL, NULL, NULL, 'DIA''s Summer Internship Program provides promising undergraduate and graduate students the opportunity to gain practical work experience in the areas of analysis, research, report writing, oral briefings, policy development, program management and computer applications related to the intelligence field. Interns are appointed for a 10-to 12-week period of work between June and August. During this time, they serve in a full-time, temporary employee status. All interns must be granted a security clearance and successfully pass a drug screening test prior to being made a final offer.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (83, NULL, NULL, NULL, '政府组织', NULL, NULL, 'Academic Summer Internship Program', '否', '非学位项目', 'https://apply.intelligencecareers.gov/student-tiles', NULL, NULL, NULL, NULL, NULL, '1 Semester', NULL, NULL, NULL, 'Academic Semester interns are appointed for one semester, from August to December. Students are employed as part-time temporary employees (working a maximum of 29 hours per week). Eligibility requirements are the same, regardless of pay status. All interns must be granted a security clearance and must successfully pass a drug screening test prior to being made a final offer. Academic Semester interns are only compensated for time worked.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (84, NULL, NULL, NULL, '政府组织', NULL, NULL, 'Department of Energy Community College Internship', '否', '非学位项目', 'https://orise.orau.gov/ornl/undergraduates/cci/default.html', NULL, NULL, NULL, NULL, NULL, '10 Weeks', NULL, NULL, NULL, 'Oak Ridge National Laboratory participates in the Department of Energy Community College Internships (CCI). The program is sponsored and managed by the DOE Office of Science’s Office of Workforce Development for Teachers and Scientists (WDTS) in collaboration with the DOE national laboratories. The program seeks to encourage community college students to enter technical careers relevant to the DOE mission by providing 10-week internships at a DOE laboratory.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (85, NULL, NULL, NULL, '私人机构', NULL, NULL, 'RoboCup', '否', '非学位项目', 'https://robocup.org/', NULL, NULL, NULL, NULL, NULL, 'Variable Time', NULL, NULL, NULL, 'In the history of artificial intelligence and robotics, the year 1997 will be remembered as a turning point. In May 1997, IBM Deep Blue defeated the human world champion in chess. Forty years of challenge in the AI community came to a successful conclusion. On July 4, 1997, NASA’s MARS Pathfinder mission made a successful landing and the first autonomous robotics system, Sojourner, was deployed on the surface of Mars. Together with these accomplishments, RoboCup made its first steps toward the development of robotic soccer players which can beat a human World Cup champion team.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (86, NULL, NULL, NULL, '政府组织', NULL, NULL, 'Graduate Research Fellowship Program', '否', '非学位项目', 'https://www.nsf.gov/pubs/2018/nsf18573/nsf18573.htm', NULL, NULL, NULL, NULL, NULL, 'Length Of Degree', NULL, NULL, NULL, 'The purpose of the NSF Graduate Research Fellowship Program (GRFP) is to help ensure the vitality and diversity of the scientific and engineering workforce of the United States. The program recognizes and supports outstanding graduate students who are pursuing full-time research-based master''s and doctoral degrees in science, technology, engineering, and mathematics (STEM) or in STEM education. The GRFP provides three years of support for the graduate education of individuals who have demonstrated their potential for significant research achievements in STEM or STEM education. NSF especially encourages women, members of underrepresented minority groups, persons with disabilities, veterans, and undergraduate seniors to apply.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (87, NULL, NULL, NULL, '政府组织', NULL, NULL, 'AMS Graduate Fellowships', '否', '非学位项目', 'https://www.ametsoc.org/index.cfm/ams/information-for/students/ams-scholarships-and-fellowships/ams-graduate-fellowships/', NULL, NULL, NULL, NULL, NULL, 'Length Of Degree', NULL, NULL, NULL, 'The AMS Fellowship Program is a source of unique opportunities for outstanding students looking to pursue graduate education in the atmospheric or related sciences. To date, 368 students have been designated as AMS fellowship recipients. The program helps these first-year graduate students to be educated about unique challenges facing the world so that they may better tackle real-world issues after graduation. Fellowships come with several benefits that include financial support, the opportunity to be special guests at the AMS Annual Meeting with exclusive events therein, and ongoing academic and career support from AMS.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (88, NULL, NULL, NULL, '政府组织', NULL, NULL, 'DHS Cybersecurity Internship Program', '否', '非学位项目', 'https://www.dhs.gov/homeland-security-careers/cybersecurity-internship-program', NULL, NULL, NULL, NULL, NULL, 'Summer', NULL, NULL, NULL, 'The Cybersecurity Internship Program is designed to give current students an opportunity to work alongside cyber leaders with the U.S. Department of Homeland Security. Interns are recruited from the nation''s top undergraduate and graduate programs to put their academic achievements and intellect to use during a critical time in American history. Interns will have the opportunity to apply concepts, protocols and tools acquired through coursework in the real world by working side by side with experts in cybersecurity. Internships focus on mission areas such as identification and analysis of malicious code, forensics analysis, incident handling, intrusion detection and prevention, and software assurance. Interns will have the opportunity to be selected into the bachelor''s or master''s-based Cyber Fellows Program within the DHS Secretary''s Honors Program after graduation.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (89, NULL, NULL, NULL, '政府组织', NULL, NULL, 'EPP/MSI Undergraduate Scholarship Program', '否', '非学位项目', 'https://www.noaa.gov/office-education/epp-msi/undergraduate-scholarship', NULL, NULL, NULL, NULL, NULL, 'Summer', NULL, NULL, NULL, 'The EPP/MSI Undergraduate Scholarship provides funds for two years of undergraduate study to rising junior undergraduate students majoring in Science, Technology, Engineering and Mathematics (STEM) fields that directly support NOAA''s mission. Participants conduct research at a NOAA facility during two paid summer internships. Since 2001, 219 students have completed the program and over 75% go on to graduate school.', NULL, NULL, NULL, NULL, NULL, NULL, NULL);
