INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (148, '墨尔本大学', 'University of Melbourne', '墨尔本大学（The University of Melbourne），1853年始建于澳大利亚文化、艺术与工业中心墨尔本，享誉世界的公立研究型大学，多榜位居全澳第1，为环太平洋大学联盟、亚太国际贸易教育暨研究联盟、国际大学气候联盟、英联邦大学协会与澳大利亚八校联盟成员，AACSB及EQUIS认证成员，U21大学联盟创始会员和秘书处所在地。墨尔本大学强调学生在学术造诣与人格修养等方面的综合能力，毕业生竞争力位居世界第7位，拥有会计学、金融学、法学、医学、教育学与心理学等超过二十个学科位居世界前20，计算机科学位居世界第32位。建校以来，墨尔本大学培养出数十万遍布世界的优秀校友，包括8位诺贝尔奖得主（全澳第1）、120位罗德学者（全澳第1）、4位澳大利亚总理、5位澳大利亚总督等各领域知名人士。墨尔本大学与清华大学、北京大学、复旦大学、上海交通大学、浙江大学、南京大学、中国人民大学、南开大学等高校均开设联合培养与合作项目。', '大学（公立）', '澳大利亚', 26, 'Software Engineering-Artificial Intelligence', '否', '学术型硕士', 'https://study.unimelb.edu.au/find/courses/graduate/master-of-software-engineering/', 'Faculty of Engineering and Information Technology', 'School of Computing and Information Systems', 'EA工程认证', 'https://study.unimelb.edu.au/find/courses/graduate/master-of-software-engineering/how-to-apply/#nav', 'Master of Software Engineering', '1 Year to Less than 5 Years', '否', '300 credit points', NULL, NULL, 'On completion of this course, graduates will:

have gained knowledge and practice in software engineering topics including software processes, project management, requirement analysis, modelling, design, architecture, implementation and testing;
have gained knowledge and practice in advanced software engineering topics which include designing secure and reliable software, high integrity systems, distributed systems and advanced software architectures;
be able to apply their knowledge to plan, manage, analyse, design and implement software products using appropriate processes;
have developed problem solving and trouble shooting skills that may be applied in professional practice;
be able to demonstrate proficiency over established and emerging engineering methods and tools to solve practical engineering problems;
be able to explain the basic principles underlying the management of physical, human and financial resource;
be able to effectively work in teams to solve complex, open-ended software engineering problems that require significant research and exploration;
have effective verbal and written communication skills that enable them to make a meaningful contribution to the changes facing society;
be conversant with important issues relevant to sectors influenced by software engineering, such as the sustainability of resources, the efficient operation of all processes and privacy and security in the age of the internet; and,
know and epitomize professional ethical behaviour and responsibilities towards their profession and the community, including having positive and responsible approaches to sustainable development, process and personal safety, management of information and professional integrity.', '否', '是', '否', 'https://handbook.unimelb.edu.au/2024/courses/mc-softeng/course-structure', 'https://cis.unimelb.edu.au/research/artificial-intelligence#people', 'https://handbook.unimelb.edu.au/courses/mc-softeng');
