INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (171, '香港城市大学', 'City University of Hong Kong', '香港城市大学（City University of Hong Kong），简称城大（CityU），是中国香港一所国际化的公立研究型综合大学，位于九龙塘，香港特别行政区政府资助的八所高等教育院校之一，为粤港澳高校联盟、粤港澳高校智慧校园联盟、京港大学联盟、中俄工科大学联盟、沪港大学联盟成员高校。城大为1984年在九龙旺角建立的香港城市理工学院，1995年更名为香港城市大学并迁往现校址九龙塘，1997年在原威灵顿中学旧址设立九龙湾分校，提供副学士及高级文凭课程。2001年，于内地设立香港城市大学深圳研究院。2013年，设立香港城市大学成都研究院。香港城市大学商学院获AACSB和EQUIS双重认证。香港城市大学位于九龙塘的校园总面积共15.6公顷，学校开设7个学院的130多个专业学科和1个研究生院，在校学生约20000人，其中6500人是研究生，教职员工逾1000人。', '大学（公立）', '中国香港', 39, 'Computer Science', '否', '学士', 'https://www.cs.cityu.edu.hk/academic-programmes/bsc-computer-science/aims', 'College of Engineering', 'Department of Computer Science', 'HKIE认证', 'https://www.cityu.edu.hk/admo/admissions/international-admissions', 'Bachelor of science in Computer science', '4 years', '否', '120units', NULL, '该专业的教育目标是培养具备以下素质的毕业生：具有扎实的计算机科学技术基础，能够创造性地将计算机及相关技术应用于实际问题沟通和软技能，以发挥有效的专业作用具备专业领域的知识，了解广泛的多学科问题，并有能力继续进行个人发展以跟上计算机技术的进步', '成功完成该专业后，学生应能够：将相关数学和工程方法应用于计算；使用计算机编程解决问题；识别问题，分析需求并制定设计以满足现实的限制，例如成本、运营、社会、文化、道德、健康和安全；使用软件工程方法和工具来开发和实施高质量的软件解决方案；有效地沟通和使用语言；有效、独立地管理项目；应用计算机科学选定领域的专业知识；反思全球社会中职业公民所需的道德和社会责任；了解当代计算问题并认识到终身学习的必要性并能够参与终身学习；在团队工作环境中（包括多学科团队）进行协作并有效发挥作用；在工业环境中有效发挥作用并将所学技能应用于现实世界的问题；获得好奇的态度和技能，从而能够创造与计算相关的原创发现或设计。', '否', '是', '否', 'https://www.cs.cityu.edu.hk/system/files?file=CIR-CMN-BSCCS-2022-23%20%26%20thereafter_Updated%20%282022.06.06%29_0.pdf', 'https://www.cs.cityu.edu.hk/people/academic-staff', NULL);
