INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (177, '阿尔托大学', 'Aalto University', '阿尔托大学（Aalto University）位于芬兰，是一所古老而创新力强的世界著名高等学府。其历史可追溯到1849年建立的赫尔辛基理工大学。该校为北欧五校联盟（NordicFiveTech, 简称N5T）成员之一，以及欧洲创新与技术研究院EIT院校成员之一，是欧洲及世界享有盛誉的多学科综合性大学。阿尔托大学是由欧洲顶尖理工院校赫尔辛基理工大学（Helsinki University of Technology）和北欧最大的艺术类院校赫尔辛基艺术设计大学（The University of Art and Design Helsinki）以及全欧洲第一所商学院赫尔辛基经济学院（The Helsinki School of Economics）三所在各自领域著名的大学合并建成。校名是为表彰和纪念在科技、经济及艺术领域为人类作出重大贡献的芬兰籍世界著名建筑大师阿尔瓦·阿尔托校友。现任校长为世界杰出计算机与人工智能领域专家Ilkka Niemelä教授。其校友包括梅林变换发明者亚尔马·梅林以及华人校友生物燃料电池发明者张霞昌博士等。', '大学（公立）', '芬兰', 41, 'Computer Science', '否', '学士', 'https://www.aalto.fi/fi/koulutustarjonta/tietotekniikka-tekniikan-kandidaatti-ja-diplomi-insinoori', 'School of Science', 'Department of Computer Science', NULL, 'https://www.aalto.fi/fi/hakijapalvelut/haku-kandidaattikoulutuksiin', '技术科学学士', '3 years', '否', '180units', NULL, NULL, '作为信息技术专业的学生，​​您是技术科学学士学位课程的一部分。技术学士学位毕业生拥有:扎实的编程能力。技术学士能够具体化数据处理的抽象问题，通过算法解决这些问题并将解决方案实现为计算机程序。 以解决方案为导向看待我们社会的挑战。技术学士学位可以发现问题并找到创新的解决方案。多学科的教育基地保证了技术学士能够在新领域或以新方式创造性地运用不同领域的基本思想。良好的团队和项目工作能力。技术学士能够进行团队合作，并且知道如何利用自己的优势来实现项目的成功。发展成为您选择的特殊领域的专家的技能。学士学位学习是信息技术 DI 研究的基础，您可以更深入地研究所选的专业领域，并最终能够从事要求严格的产品开发任务和专家角色。 ', '否', '否', '否', 'https://www.aalto.fi/fi/ohjelmat/teknistieteellinen-kandidaattiohjelma/opetussuunnitelma-2024-2026', 'https://research.aalto.fi/en/organisations/department-of-computer-science/persons/', NULL);
