INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (183, '悉尼大学', 'The University of Sydney', '悉尼大学（The University of Sydney），始建于1850年，坐落于南半球金融、贸易与旅游中心——澳大利亚新南威尔士州首府悉尼，是一所世界顶尖研究型大学，为砂岩学府、澳大利亚八校联盟、环太平洋大学联盟、世界大学联盟、英联邦大学协会，全球高校人工智能学术联盟、新工科教育国际联盟、亚太国际贸易教育暨研究联盟的成员。同时也是AACSB、AMBA和EQUIS认证大学及欧洲全球管理教育联盟CEMS成员（每个国家地区仅一所大学入选）。悉尼大学注重理论与实践相结合，教育，法学，医学，会计与金融 ，政治与国际研究，建筑，音乐，药学和药理学，护理学等专业均位列2019QS世界前20。悉尼大学历史上培养了一系列的重要人物，其毕业生一直牢牢掌控着澳大利亚政治与经济命脉，包括8位澳大利亚总理、5位诺贝尔奖得主，23位最高法院法官、101名罗德学者、联合国大会主席、国际法院院长、世界银行总裁、澳大利亚储备银行行长，麦格理集团总裁等知名政商界人士。依托于极高的学术声誉和雇主评价，悉尼大学作为澳洲本土生源第一学府的记录已保持数十年。牛津大学前法学院院长迈克尔·斯宾塞就任悉尼大学校长一职。悉尼大学目前与全球42个国家和地区的292所合作大学签署了交换生协议。', '大学（公立）', '澳大利亚', 43, 'Advanced Computing', '否', '学士', 'https://www.sydney.edu.au/courses/courses/uc/bachelor-of-advanced-computing.html', 'Faculty of Engineering ', 'School of Computer Science', NULL, 'https://sydneystudent.sydney.edu.au/sitsvision/courses/dbpadvcmp1000-0.htm', 'Bachelor of Advanced Computing', '4 years', '否', '192units', NULL, NULL, NULL, '否', '否', '否', 'https://cusp.sydney.edu.au/students/view-degree-page/degree_id/754', 'https://www.sydney.edu.au/engineering/schools/school-of-computer-science/academic-staff.html', NULL);
