INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (187, '印度理工学院卡拉普分校', 'Indian Institute of Technology Kharagpur (IIT-KGP)', '印度理工学院（Indian Institute of Technology），简称IIT，是由印度政府所建设和组成的七间自治工程与技术学院。在学术界享有世界声誉，被称为印度“科学皇冠上的瑰宝”，是印度最顶尖的工程教育与研究机构。印度理工学院培养的IT人才遍及世界各地，美国硅谷更是这些IT人才的聚集地。印度理工学院为印度软件业在世界范围内的成功做出了不可磨灭的贡献。印度理工学院创建于1951年，在全国共设有7所校区，分别是：德里理工学院、坎普尔理工学院、卡哈拉格普尔理工学院、马德拉斯理工学院、孟买理工学院、瓜哈提理工学院和卢克里理工学院。1963年，根据印度国家技术院校法案，印度理工学院被列为国家重点院校，并赋予独立的学术政策、独立的招生及学位授予权。印度理工学院在全国的7所院校均为政府大学，在教学和经济管理上由直属中央政府的印度理工学院委员会管辖。印度人力资源开发部是该委员会的主席，每个分院各设董事会负责全权管理。董事会负责各院校的学术政策的制定，教学大纲的审核及成绩考核。', '大学（公立）', '印度', 45, 'COMPUTER SCIENCE AND ENGINEERING', '否', '学士', 'https://cse.iitkgp.ac.in/?academicprograms.html', 'Faculty of Engineering and Architecture', 'Department of Computer Science and Engineering', NULL, 'https://www.iitkgp.ac.in/jee-ug', 'Bachelor of Technology', '4 years', '否', '127units', NULL, NULL, NULL, '否', '否', '否', 'https://erp.iitkgp.ac.in/ERPWebServices/curricula/CurriculaSubjectsList.jsp?stuType=UG&splCode=CS&curr_type=NEW', 'https://cse.iitkgp.ac.in/?faculty.html', NULL);
