INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (189, '哈尔滨工业大学', 'Harbin Institute of Technology', '哈尔滨工业大学（Harbin Institute of Technology），由工业和信息化部直属的全国重点大学，位列国家首批世界一流大学建设高校A类、985工程、211工程，九校联盟、环太平洋大学联盟、中国大学校长联谊会、卓越大学联盟、全球能源互联网大学联盟、中俄工科大学联盟、中国-西班牙大学联盟、中国人工智能教育联席会创始成员，入选珠峰计划、强基计划、2011计划、111计划、卓越工程师教育培养计划、国家大学生创新性实验计划、国家建设高水平大学公派研究生项目、新工科研究与实践项目、中国政府奖学金来华留学生接收院校、全国深化创新创业教育改革示范高校等，是首批设有研究生院、拥有研究生自主划线资格的高校，首批学位授权自主审核单位。学校始建于1920年，1951年被确定为全国学习国外高等教育办学模式的两所样板大学之一，1954年进入国家首批重点建设的6所高校行列。1996年进入国家”211工程“首批重点建设高校。1999年被确定为国家首批”985工程“重点建设的9所大学之一。2000年与哈尔滨建筑大学合并组建新的哈尔滨工业大学。2017年入选“双一流”建设A类高校名单。 截至2018年5月，哈工大有哈尔滨、威海、深圳三个校区；本部校园面积5212.35亩，建筑面积202.69万平方米；专任教师3045人，在校学生46138人。', '大学（公立）', '中国大陆', 46, '人工智能', '否', '学士', 'http://www.cs.hit.edu.cn/2018/0521/c11453a218529/page.htm', '哈尔滨工业大学计算学部', '计算机科学与技术学院', '哈工大计算机专业建于1956年，是中国最早的计算机专业之一，拥有计算机科学与技术国家一级重点学科、软件工程一级学科、网络空间安全一级学科。人才培养历史悠久，数量多、质量高，已培养本科生10449人、硕士4283人，博士822人。在教育部最新学科评估中，计算机科学与技术专业位列A档，软件工程专业位列A-档。2017年计算机学科跻身世界大学学术排行榜（ARWU）第27名、ESI 排行榜全球前1%（第48名）、USNews世界大学计算机学科排行榜第33名。', NULL, '工学学士学位', '4 years', '否', '160units', 'https://cs.hit.edu.cn/2018/0521/c11453a218530/page.htm', '目标1：身心健康，具有正确的世界观、人生观与价值观，恪守工程伦理与职业道德规范，德智体美劳全面发展；目标2：具备社会责任感、专业使命感，具有在人工智能及数字媒体技术相关领域引领未来发展的能力；目标3：具备计算思维、系统思维能力，能够综合运用计算机、数学与智能信息处理等方面知识，分析、设计各种机器学习方法，构建智能信息处理软、硬件系统，能够解决现代人工智能、数字媒体技术领域中的复杂工程技术问题；目标4：具有创新精神，具备较强智能计算相关的理论与工程创新能力；目标5：具有国际视野、学科交叉融合、团队合作、项目管理、跨文化交流、终身学习等能力。', '能够运用数学、计算机等领域的基础知识，分析、设计各种人工智能算法、软件及系统，毕业后能从事人工智能基础理论、智能信息处理、数字媒体处理技术等方向的理论研究、技术研发、应用开发、专业教育等工作，能够引领未来发展，具有国际视野、社会责任感和专业使命感', '否', '是', '否', 'http://www.cs.hit.edu.cn/2018/0521/c11453a218530/page.htm', 'http://www.cs.hit.edu.cn/11446/list.htm', NULL);
