INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (201, '澳大利亚国立大学', 'Australian National University', '澳大利亚国立大学（The Australian National University），简称ANU，坐落于澳大利亚首都堪培拉，享誉世界的顶尖高等学府。始建于1946年，是澳大利亚第一所研究型大学，也是环太平洋大学联盟、国际研究型大学联盟、澳大利亚八校联盟成员。它是唯一一所由澳大利亚联邦议会立法创立，在全澳享有国立大学资格的最高学府。ANU以极高声誉的研究主导型教学而著称，坐拥南半球藏书体系最大的图书馆，拥有南半球最先进的超级计算机Gadi，以及闻名于世的赛丁泉天文台。澳大利亚四大国家科学院、澳大利亚联邦科学与工业研究组织（CSIRO）均坐落于此。世界第一支临床医用青霉素、光子瞬间转移技术也在这里诞生。ANU于1946年创校初期仅招收研究生，1960年开始开设本科教学。教学人员中博士的比例为88%，为全澳最高。超过三分之一的澳大利亚联邦院士在此执教或从事科研。建校仅74年，校友已包括6位诺贝尔奖得主、52位罗德学者、2位澳大利亚总理、7位澳大利亚首席大法官、25位澳大利亚联邦部长、30位驻外大使以及多国政要等。ANU的自然科学、人文与社会科学领域在世界拥有崇高的地位。2016年，诺贝尔物理学奖得主布莱恩·施密特出任学校新任校长。', '大学（公立）', '澳大利亚', 50, 'Advanced Computing', '否', '学士', 'https://programsandcourses.anu.edu.au/program/AACOM', 'College of Engineering.Computing and Cybernetics', 'School of Computing', NULL, 'https://programsandcourses.anu.edu.au/program/apply/AACOM', 'Bachelor of Advanced Computing', '4 years', '否', '192units', NULL, NULL, '定义和分析复杂问题，设计、实施和评估解决方案，以表明对软件开发和运行的系统环境的理解，包括经济、社会、历史、可持续性和道德方面。展示对计算机科学基础（包括编程、算法、逻辑、架构和数据结构）的操作和理论理解。认识学科之间的联系和反复出现的主题，包括抽象和复杂性。适应新环境、新技术，并进行创新。展现对计算机科学至少一个领域的深厚知识的理解。使用多种方式与不同受众有效地传达复杂的概念。在团队中有效工作以实现共同目标。展现对职业行为和发展的承诺，认识到其工作的社会、法律和道德影响，独立工作，并自我和同事评估绩效。展示对研究方法基础的理解，包括定义研究问题、背景阅读和文献综述、设计实验和有效地传达结果。将研究方法应用于解决计算机科学当代研究问题。', '否', '是', '否', 'https://programsandcourses.anu.edu.au/program/AACOM#studyoptions', 'https://comp.anu.edu.au/people/', NULL);
INSERT INTO "degree_prog" ("degree_prog_id", "inst_name_cn", "inst_name_en", "intro_cn", "inst_type", "country", "rank", "prog_name", "is_indep", "prog_type", "prog_url", "school", "host_inst", "awards_or_abet", "apply_url", "degree", "duration", "joint", "min_credit", "thesis", "prog_goal", "prog_outcome", "has_culm_act", "internship ", "has_minor", "course_url", "faculty", "stu_hb_url") VALUES (202, '澳大利亚国立大学', 'Australian National University', '澳大利亚国立大学（The Australian National University），简称ANU，坐落于澳大利亚首都堪培拉，享誉世界的顶尖高等学府。始建于1946年，是澳大利亚第一所研究型大学，也是环太平洋大学联盟、国际研究型大学联盟、澳大利亚八校联盟成员。它是唯一一所由澳大利亚联邦议会立法创立，在全澳享有国立大学资格的最高学府。ANU以极高声誉的研究主导型教学而著称，坐拥南半球藏书体系最大的图书馆，拥有南半球最先进的超级计算机Gadi，以及闻名于世的赛丁泉天文台。澳大利亚四大国家科学院、澳大利亚联邦科学与工业研究组织（CSIRO）均坐落于此。世界第一支临床医用青霉素、光子瞬间转移技术也在这里诞生。ANU于1946年创校初期仅招收研究生，1960年开始开设本科教学。教学人员中博士的比例为88%，为全澳最高。超过三分之一的澳大利亚联邦院士在此执教或从事科研。建校仅74年，校友已包括6位诺贝尔奖得主、52位罗德学者、2位澳大利亚总理、7位澳大利亚首席大法官、25位澳大利亚联邦部长、30位驻外大使以及多国政要等。ANU的自然科学、人文与社会科学领域在世界拥有崇高的地位。2017年，诺贝尔物理学奖得主布莱恩·施密特出任学校新任校长。', '大学（公立）', '澳大利亚', 50, 'Computing', '否', '专业型硕士', 'https://programsandcourses.anu.edu.au/program/7706XMCOMP', 'ANU College of Engineering.Computing and Cybernetics', 'School of Computing', NULL, 'https://programsandcourses.anu.edu.au/program/apply/7706XMCOMP', 'Master of Computing', '2 years', '否', '96units', NULL, NULL, '专业地应用系统计算方法来解决各个领域的复杂、多学科的现实计算问题。综合并熟练应用其专业和其他选修学习领域以及基础科学和计算方法的先进、综合的技术知识。识别并批判性地评估其专业和其他选修学习领域的当前发展和新兴趋势。了解影响专业计算实践的背景因素，并确定计算活动的潜在社会、道德和环境影响。利用各种通信媒体和工具与同事、其他计算专业人员和更广泛的社区进行有效沟通。进行独立调查、批判性反思和终身学习，继续在学科的前沿实践。在跨文化、多学科团队中有效且积极主动地工作，展现自主性、道德行为、专家判断、适应性和责任感，以高标准实现计算成果。', '否', '是', '否', 'https://programsandcourses.anu.edu.au/program/7706XMCOMP', 'https://comp.anu.edu.au/people/', NULL);
